import React, { useEffect, useState } from "react";
import axios from "axios";
import ProductCard from "./ProductCard";
import SearchBar from "./SearchBar";
import FilterBar from "./FilterBar";
import CartDropdown from "./CartDropdown";
import "./ProductList.css";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState("");
  const [category, setCategory] = useState("All");
  const [rating, setRating] = useState("0");
  const [priceRange, setPriceRange] = useState("All");
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    console.log("üîÑ Fetching products from backend...");
    axios.get("http://localhost:5000/api/products")
      .then((res) => {
        setProducts(res.data);
        console.log("‚úÖ Products loaded:", res.data);
      })
      .catch((err) => {
        console.error("‚ùå Error fetching products:", err);
      });
  }, []);

  const handleAddToCart = (product) => {
    setCartItems((prev) => [...prev, product]);
  };

  const handleRemoveFromCart = (id) => {
    setCartItems((prev) => prev.filter((item) => item._id !== id));
  };

  const filtered = products.filter((p) => {
    const matchName = p.name.toLowerCase().includes(search.toLowerCase());
    const matchCategory = category === "All" || p.category === category;
    const matchRating = parseFloat(p.rating) >= parseFloat(rating);
    const matchPrice =
      priceRange === "All" ||
      (priceRange === "0-999" && p.price <= 999) ||
      (priceRange === "1000-1999" && p.price >= 1000 && p.price <= 1999) ||
      (priceRange === "2000" && p.price >= 2000);

    return matchName && matchCategory && matchRating && matchPrice;
  });

  return (
    <div className="product-page">
      <div className="header">
        <h1>üõçÔ∏è Product Listing</h1>
        <div className="cart-icon" onClick={() => setShowCart(!showCart)}>
          üõí <span className="cart-count">{cartItems.length}</span>
        </div>
      </div>

      {showCart && (
        <CartDropdown
          cartItems={cartItems}
          onRemoveFromCart={handleRemoveFromCart}
        />
      )}

      <SearchBar search={search} setSearch={setSearch} />

      <FilterBar
        category={category}
        setCategory={setCategory}
        rating={rating}
        setRating={setRating}
        priceRange={priceRange}
        setPriceRange={setPriceRange}
      />

      <div className="product-grid">
        {filtered.map((p) => (
          <ProductCard key={p._id} product={p} onAddToCart={handleAddToCart} />
        ))}
      </div>
    </div>
  );
};

export default ProductList;
