// App.js
import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import ProductList from "./components/ProductList";
import './App.css';

function Home() {
  return (
    <div className="home-container">
      <h1>üõçÔ∏è Welcome to Product World</h1>
      <p className="subtitle">Discover a wide range of stylish and affordable products.</p>
      <Link to="/products">
        <button className="home-btn">Explore Products ‚Üí</button>
      </Link>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/products" element={<ProductList />} />
      </Routes>
    </Router>
  );
}

export default App;
